(function(exports) {
	exports.parse = function(cookie) {
		var cookies = {};

		cookie && cookie.split(';').forEach(function(cookie) {
			var parts = cookie.split('=');

			cookies[parts[0].trim()] = (parts[1] || '').trim();
		});

		return cookies;
	};

	exports.stringify = function(cookies) {
		if(!cookies) {
			return '';
		}

		var s = '';

		if(!cookies.length) {
			cookies = [cookies];
		}
		var cs = [];
		for (var i in cookies) {
			for (var key in cookies[i]) {	
				s += key + '=' + cookies[i][key] + ';';
			}
			return s;
		}
		return cs;
	}
})(exports);